module.exports = function(gulp, options, plugins) {
    gulp.task('bin-build', function() {
        return gulp
            .src('src/bin/*.js')
            .pipe(plugins.cached('bin'))
            .pipe(plugins.babel({
                presets: options.babel
            })).on('error', function(err) {
                console.error(err.toString());
                this.emit('end');
            })
            .pipe(plugins.uglify({
                compress: {
                    drop_console: true,
                    global_defs: {
                      "DEBUG": false,
                      "RELEASE": true
                    }
                }
            }))
            .pipe(gulp.dest(plugins.path.join('bin')));
    });
};